arguments.argument:
{
    if(readyToShoot == true and BladesOut<MaxBlades)  {
        playsound(x,y,BladeSnd);
        var shot;
        randomize();
            shot = instance_create(x,y,BladeB);
            shot.direction=owner.aimDirection;
            shot.speed=12;
            shot.owner=owner;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            BladesOut += 1;
            shot.weapon=WEAPON_BLADE2;
            with(shot) {
                hspeed+=owner.hspeed;
            }
        readyToShoot=false;
        alarm[0]=reloadTime;
    }
}
