arguments.argument:
{
    if(readyToShoot) {
        playsound(x,y,MedichaingunSnd);
        var shot;
        randomize();

        shot = instance_create(x,y + yoffset + 1,Needle);
        shot.direction=owner.aimDirection+random(4)-2;
        shot.speed=8;
        shot.owner=owner;
        shot.ownerPlayer=ownerPlayer;
        shot.team=owner.team;
        shot.hitDamage = 2.6;
        shot.weapon=WEAPON_NEEDLEGUN;
        with(shot) {
            hspeed+=owner.hspeed;
            //motion_add(owner.direction, owner.speed);
        }
        readyToShoot=false;
        alarm[0]=reloadTime;
    }
}
