arguments.argument:
if (other.team != team)
{
    if(speed > 0)
    {
        other.solid = true;
        move_contact_solid(direction, speed);
        move_outside_solid(direction+180, speed);
        if(!place_free(x+sign(hspeed), y))
            hspeed = 0;
        if(!place_free(x, y+sign(vspeed)))
            vspeed = 0;
        if(!place_free(x+sign(hspeed), y+sign(vspeed)))
            speed = 0;
        other.solid = false;
    }
    else
    {
        other.solid = true;
        move_outside_solid(point_direction(other.x, other.y, x, y), point_distance(other.x, other.y, x, y));
        if(!place_free(x+sign(hspeed), y))
            hspeed = 0;
        if(!place_free(x, y+sign(vspeed)))
            vspeed = 0;
        if(!place_free(x+sign(hspeed), y+sign(vspeed)))
            speed = 0;
        other.solid = false;
    }
}
