arguments.argument:
{
    var xr, yr, charAlpha;
    xr = round(x);
    yr = round(y);

    if (global.myself == -1) {
        charAlpha = 1;
        exit;
    } else if((global.myself.object == id)/* || (global.myself.object == -1)*/) {
        charAlpha = 1;
    } else if (!invisible) {
        charAlpha = cloakAlpha;
    } else {
        exit;
    }
    if global.myself.team == team and cloak and global.myself.class != CLASS_SPY {
            cloakAlpha = 0.5;
            charAlpha = 0.5;
            if !currentWeapon.readyToStab {
                cloakAlpha = 0;
                charAlpha = 0;
                        }            
                    }
    if(bubbleAlpha > 0) {
        draw_sprite_ext(BassieS, bubbleImage, xr+10, yr-18, 1, 1, 0, c_white, charAlpha*bubbleAlpha);
    }

    draw_set_alpha((charAlpha+1)/2);

     if (charge = true && image_xscale =1 && global.myself == player) {
    draw_healthbar(xr+30, yr-40, xr+50, yr-35,currentWeapon.hitDamage*100/currentWeapon.maxDamage,c_black,c_yellow,c_red,0,true,true);
    }else{
    if (charge = true && image_xscale = -1 && global.myself == player) {
    draw_healthbar(xr-30, yr-40, xr-50, yr-35,currentWeapon.hitDamage*100/currentWeapon.maxDamage,c_black,c_yellow,c_red,0,true,true);
    }
    }
    if (hp>maxHp) {
    draw_healthbar(xr-10, yr-30, xr+10, yr-25,(hp-maxHp)*100/maxHp*2,c_green,c_olive,c_lime,0,true,true);
    }else{
    draw_healthbar(xr-10, yr-30, xr+10, yr-25,hp*100/maxHp,c_black,c_red,c_green,0,true,true);}
    if(distance_to_point(mouse_x, mouse_y)<25) {
        draw_set_halign(fa_center);
        draw_set_valign(fa_bottom);
        if(team==TEAM_RED) {
            draw_set_color(c_red);
        } else {
            draw_set_color(c_blue);
        }
        draw_text(xr, yr-35, player.name);
    }  
    draw_set_alpha(1);
    draw_sprite_ext(sprite_index,floor(animationImage+animationOffset),xr,yr,image_xscale,image_yscale,image_angle,c_white,cloakAlpha);
}
