arguments.argument:
{
  // check if it matches the server's copy
  if(CustomMapGetMapMD5(global.currentMap) == global.currentMapMD5) {
    // we already have the exact same map, so we'll jump into the game
    room_goto_fix(CustomMapRoom);
    exit;
  } else {
    // our map isn't the same as the server's copy
    // Tell the user, and disconnect from the server
    show_message("Server's copy of the map differs from ours. Exiting");
    game_end();
  }
}
