arguments.argument:
{
    if(sticksTo == -1 and other.id != ownerPlayer.object and other.team != team) {
        other.hp -= hitDamage;
        other.lastDamageDealer = ownerPlayer;
        other.lastDamageSource = weapon;
        if(object_is_ancestor(other.object_index, Pyro) or (other.flamecount>7)) {
            instance_destroy();
        } else {
            sticksTo = other.id;
            stickRelativeX = x-other.x;
            stickRelativeY = y-other.y;
            speed=0;
            alarm[0]=150;
            other.flamecount += 1;
        }
    }
}
