arguments.argument:
{
    if(sticksTo == -1) {
        vspeed-=0.1;
        image_alpha*=0.95;
        if speed>12 then speed=12;
    } else {
        image_alpha=0.3;
        if(instance_exists(sticksTo)) { 
            sticksTo.hp -= burnDamage;
            sticksTo.lastDamageDealer = ownerPlayer;
            sticksTo.lastDamageSource = weapon;

            x = sticksTo.x+stickRelativeX*sticksTo.image_xscale;
            y = sticksTo.y+stickRelativeY;
        } else {
            instance_destroy();
        }
    }
    if(random(8)<1) effect_create_below(ef_smokeup,x,y-8,0,c_gray);
}
