arguments.argument:
speed = min(speed, 15);

//let the burning begin
{
    if (burnDuration > 0)
    {
        burnDuration -= durationDecay;
        hp -= burnIntensity / 30;
        if (lastDamageDealer != burnedBy && lastDamageDealer != player) {
            secondToLastDamageDealer = lastDamageDealer;
            alarm[4] = alarm[3];
        }
        alarm[3] = ASSIST_TIME;
        lastDamageDealer = burnedBy;
        lastDamageSource = WEAPON_FLAMETHROWER;
    }
    if (alarm[0] == -1 && burnIntensity > 0) burnIntensity -= intensityDecay;
    if (burnDuration <= 0 || burnIntensity <= 0)
    {
        burnDuration = 0;
        burnIntensity = 0;
        burnedBy = -1;
    }
}
