arguments.argument:
var xoffset, yoffset, xsize, ysize;
xoffset = view_xview[0];
yoffset = view_yview[0];

//Slider
if (mouse_check_button_pressed(mb_left))
{
    //draw_rectangle(xoffset + 375,yoffset + 325,xoffset + 390 + slider_offset, yoffset + 375 + slider_offset,false);
    if (mouse_x > xoffset + 380 and mouse_x < xoffset + 395)
        if (mouse_y > yoffset + 225 + slider_offset and mouse_y < yoffset + 275 + slider_offset)
        {
            slide = true;
            mPos_y = yoffset - mouse_y;
            last_offset = slider_offset;
        }
}
if (slide)
{
    if (!mouse_check_button(mb_left))
    {
        slide = false;
        exit;
    }
    slider_offset = last_offset + (mPos_y - (yoffset - mouse_y));
}
// 0 <= slider_offset <= 180
slider_offset = min(180, max(0, slider_offset));
