arguments.argument:
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];

if editing!=-1{
    if editing <=10 value[editing] = keyboard_string;
    else if editing = 11 {
        for (i=0;i<10;i+=1) {
        value[i]=-1
        global.classlimit[i]=-1
        }
        event_user(15)
    }
}
else if canClick == true && editing == -1 && mouse_x > xbegin and mouse_x < xbegin+width {
    virtualitem = round((mouse_y-ybegin)/spacing);
    if(mouse_check_button_pressed(mb_left)){
        if(virtualitem >= 0 and virtualitem < items) {
            keyboard_string="";
            event_user(virtualitem);
        }
    }
} else {
    virtualitem = -1;
}
