arguments.argument:
if mouse_x > x and mouse_x < x+width and mouse_y > y and mouse_y < y+height
{
    sizeOfCompartiment = height/numItems
    for (i=0; i<numItems; i+=1)
    {
        if mouse_y > y+(i*sizeOfCompartiment) and mouse_y < y+((i+1)*sizeOfCompartiment)
        {
            // Mouse is inside this compartiment
            virtualItem = i
            break;
        }
    }
}
else
{
    virtualItem = -1
}
