arguments.argument:
{
    menu_create(48, 172, 500, 260, 32, 40, 104, 6);
    menumode = true;

    if (room != Options)
        menu_setdimmed();

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_keyormouse(_("Primary Fire:"), "global.attack");
    menu_addedit_keyormouse(_("Secondary Ability:"), "global.special");
    menu_addedit_key(_("Emoticon Menu:"), "global.chat1");
    menu_addedit_key(_("Gameplay Emote Menu:"), "global.chat2");
    menu_addedit_key(_("Direction Emote Menu:"), "global.chat3");
    menu_addedit_key(_("Change Team:"), "global.changeTeam");
    menu_addedit_key(_("Change Class:"), "global.changeClass");

    menu_addtablink(_("Ingame"), '
        instance_destroy();
        instance_create(x,y,OptionsController);
    ');
    menu_addtablink(_("Engine"), '
        instance_destroy();
        instance_create(x,y,EngineOptionsController);
    ');
    menu_addtablink(_("Controls 1"), '
        instance_destroy();
        instance_create(x,y,ControlsController);
    ');
    menu_addtablink(_("Controls 2"), '
        instance_destroy();
        instance_create(x,y,AdvControlsController);
    ');
    menu_addtablink(_("HUD"), '
        instance_destroy();
        instance_create(x,y,HUDOptionsController);
    ');

    menu_addback(_("Back"), '
        instance_destroy();
        if(room == Options)
            room_goto_fix(Menu);
        else
            instance_create(0,0,InGameMenuController);
    ');
}
