arguments.argument:
{
    menu_create(48, 172, 500, 260, 32, 40, 104, 6);
    menumode = true;

    if(room != Options)
        menu_setdimmed();

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_select(_("Timer Position"), "global.timerPos", '
        gg2_write_ini("Settings", "Timer Position", argument0);
    ');
    menu_add_option(0, _("Center"));
    menu_add_option(1, _("Left"));

    menu_addedit_select(_("Kill Log Position"), "global.killLogPos", '
        gg2_write_ini("Settings", "Kill Log Position", argument0);
    ');
    menu_add_option(0, _("Default"));
    menu_add_option(1, _("Just Below Timers"));

    menu_addedit_select(_("KOTH Timer Orientation"), "global.kothHudPos", '
        gg2_write_ini("Settings","KoTH HUD Position", argument0);
    ');
    menu_add_option(0, "Default");
    menu_add_option(1, "Stacked");

    menu_addtablink(_("Ingame"), '
        instance_destroy();
        instance_create(x,y,OptionsController);
    ');
    menu_addtablink(_("Engine"), '
        instance_destroy();
        instance_create(x,y,EngineOptionsController);
    ');
    menu_addtablink(_("Controls 1"), '
        instance_destroy();
        instance_create(x,y,ControlsController);
    ');
    menu_addtablink(_("Controls 2"), '
        instance_destroy();
        instance_create(x,y,AdvControlsController);
    ');
    menu_addtablink(_("HUD"), '
        instance_destroy();
        instance_create(x,y,HUDOptionsController);
    ');

    menu_addback(_("Back"), '
        instance_destroy();
        if(room == Options)
            room_goto_fix(Menu);
        else
            instance_create(0,0,InGameMenuController);
    ');
}
