arguments.argument:
{
    if(readyToShoot) {
        playsound(x,y,ChaingunSnd);
        var shot;
        randomize();
            shot = instance_create(x+lengthdir_x(20,owner.aimDirection),y+12+lengthdir_y(20,owner.aimDirection),Shot);
            shot.direction=owner.aimDirection+(random(15)-7);
            shot.speed=12+random(3);
            shot.owner=owner;
            shot.pushBack=0.065;
            shot.lifeTime = 36;
            shot.hitDamage=5.5;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            shot.weapon=WEAPON_MINIGUN;
            with(shot) {
                hspeed+=owner.hspeed;
                //motion_add(owner.direction, owner.speed);
            }
        readyToShoot=false;
        alarm[0]=reloadTime;
    }
}
