var redIsCapping, blueIsCapping;
//redIsCapping = !instance_exists(IntelligenceBlue) || (IntelligenceBlue.x != IntelligenceBaseBlue.x || IntelligenceBlue.y != IntelligenceBaseBlue.y);
//blueIsCapping = !instance_exists(IntelligenceRed) || (IntelligenceRed.x != IntelligenceBaseRed.x || IntelligenceRed.y != IntelligenceBaseRed.y); 
redIsCapping = !instance_exists(IntelligenceBlue);
blueIsCapping = !instance_exists(IntelligenceRed);
if !redIsCapping { redIsCapping = (IntelligenceBlue.x != IntelligenceBaseBlue.x || IntelligenceBlue.y != IntelligenceBaseBlue.y); }
if !blueIsCapping { blueIsCapping = (IntelligenceRed.x != IntelligenceBaseRed.x || IntelligenceRed.y != IntelligenceBaseRed.y); }

if (timer > 0) { 
    timer -= 1;
    if (global.redCaps == global.caplimit && (!blueIsCapping || global.redCaps >= global.blueCaps + 2)) { global.winners = TEAM_RED; exit; }
    if (global.blueCaps == global.caplimit && (!redIsCapping || global.blueCaps >= global.redCaps + 2)) { global.winners = TEAM_BLUE; exit; }
    if (global.redCaps == global.caplimit && global.blueCaps == global.caplimit && !blueIsCapping && !redIsCapping) { global.winners = TEAM_SPECTATOR; exit }
}
if (timer == 0) {
    if (global.redCaps >= global.blueCaps + 2) { global.winners = TEAM_RED; exit; }
    if (global.blueCaps >= global.redCaps + 2) { global.winners = TEAM_BLUE; exit; }

    if (redIsCapping) {
        if (global.blueCaps - global.redCaps >= 2) { global.winners = TEAM_BLUE; exit; }
        if (global.blueCaps < global.redCaps) { global.winners = TEAM_RED; exit; }
    }
    if (blueIsCapping) {
        if (global.redCaps - global.blueCaps >= 2) { global.winners = TEAM_RED; exit; }
        if (global.redCaps < global.blueCaps) { global.winners = TEAM_BLUE; exit; }
    }
    if instance_exists(IntelligenceRed) && instance_exists(IntelligenceBlue) {
        if IntelligenceRed.x == IntelligenceBaseRed.x && IntelligenceRed.y == IntelligenceBaseRed.y &&
        IntelligenceBlue.x == IntelligenceBaseBlue.x && IntelligenceBlue.y == IntelligenceBaseBlue.y {
            if global.redCaps > global.blueCaps global.winners = TEAM_RED;
            else if global.blueCaps > global.redCaps global.winners = TEAM_BLUE;
            else global.winners = TEAM_SPECTATOR;
        }
        else overtime = 1;
    }
    else overtime = 1;      
}
