arguments.argument:
if(stickied)
{
    if (reflector != noone and alarm[0] < 0)
        alarm[0] = 30 / global.delta_factor;
    splashThreshhold = false;
}
else
{
    vspeed += 0.2 * global.delta_factor;
    vspeed = min(vspeed,8);
    splashThreshhold = true;
}

x += hspeed * global.delta_factor;
y += vspeed * global.delta_factor;
x -= hspeed;
y -= vspeed;
