arguments.argument:
{
    if(readyToShoot) {
        playsound(x,y,ShotgunSnd);
        var shot;
        randomize();
        repeat(8) {
            shot = instance_create(x,y,Shot);
            shot.direction=owner.aimDirection+(random(31)-15);
            shot.speed=10+random(6);
            shot.hitDamage = 6;
            shot.lifeTime=25;
            shot.alarm[0]=15;
            shot.pushBack=0.015;
            shot.owner=owner;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            shot.weapon=WEAPON_SCATTERGUN;
            with(shot) {   
                hspeed+=owner.hspeed;
                if speed>18 then speed=20-random(5)
                //motion_add(owner.direction, owner.speed);
            }
        }
        readyToShoot=false;
        alarm[0]=reloadTime;
    }
}
