if team == TEAM_RED sprite_index=SentryRed;
if owner.sentryBuilt==0 instance_destroy();

if hspeed == 0 && vspeed == 0 && built==0 {
image_speed = 0.07;
hp+=1;
}
if  image_index >= 7 && built ==0{
