    currentWeapon = instance_create(x,y+1,weapons[0]);
    currentWeapon.owner=owner;
    currentWeapon.ownerPlayer=ownerPlayer;
    currentWeapon.team=team;
    if image_xscale=1 currentWeapon.direction=0;
    if image_xscale=-1 currentWeapon.direction=180;

}


if(place_free(x,y+1)) {
        vspeed += 0.6;
        }

