arguments.argument:
    if(((direction+270) mod 360)>180) {
        image_xscale=1;
        image_angle = direction;
    } else {
        image_xscale=-1;
        image_angle = direction+180;
    }

NearestPlayer = -1;
distance = 9001;
totalPlayers = instance_number(Character);
var char;
for(i=0;i<totalPlayers;i+=1){
char = instance_find(Character,i)
if  collision_line(x,y,char.x,char.y,Obstacle,true,true)<0 && !char.cloak && char.team != owner.team && distance_to_object(char) < distance {
    NearestPlayer=char;
    distance=distance_to_object(char);
    }
}


if NearestPlayer == -1 exit;
//if collision_line(x,y,NearestPlayer.x,NearestPlayer.y,Obstacle,true,true)>=0 exit;

direction = point_direction(x,y,NearestPlayer.x,NearestPlayer.y);
if distance_to_point(NearestPlayer.x,NearestPlayer.y) < 375 {
    if(readyToShoot) {
        playsound(x,y,ShotgunSnd);
        var shot;
            shot = instance_create(x,y,Shot);
            shot.direction=direction+(random(11)-5);
            shot.speed=22+random(5);
            shot.lifeTime=30;
            shot.pushBack=0.08;
            shot.damage=9;
            shot.owner=owner;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            shot.weapon=WEAPON_SENTRYTURRET;

        readyToShoot=false;
        alarm[0]=reloadTime;
}
}
