arguments.argument:
{
if(place_free(x,y+1)) {
vspeed += 0.4;
        }
if(vspeed>9) {
vspeed=9;
    }
if(vspeed<-9) {
vspeed=-9;
    }
if(hspeed>9) {
hspeed=9;
    }
if(hspeed<-9) {
hspeed=-9;
    }
if point_distance(x,y,view_xview[0],view_yview[0])>2000 {
instance_destroy();
        }
   image_alpha -= (10/lifetime);
}
