arguments.argument:
// Make self invisible if behind enemy
invisible = false;

if (global.myself != -1) {
    if (global.myself.object != -1) {
        with (global.myself.object)
        {
            if ((team != other.team) && (sign(other.x - x) == -sign(image_xscale))) // Passive
                other.invisible = true;
            else // LOS
            {
                if(collision_line(x, y-16, other.x, other.y, Obstacle, true, true) and
                    collision_line(x, y-16, other.x, other.y-8, Obstacle, true, true) and
                    collision_line(x, y-16, other.x, other.y-16, Obstacle, true, true) and
                    collision_line(x, y-16, other.x, other.y+8, Obstacle, true, true) and
                    collision_line(x, y-16, other.x, other.y+16, Obstacle, true, true) and
                    collision_line(x, y-16, other.x, other.y+24, Obstacle, true, true))
                    other.invisible = true;

            }
        }
    }
}
