arguments.argument:
{
    var offset;
    if(alarm[0] == reloadTime) {
        offset=2;
    } else {
        offset=0;
    }
    if (!owner.invisible) {
        draw_sprite_ext(sprite_index,owner.team+offset,round(x+xoffset*image_xscale),round(y+yoffset),image_xscale,image_yscale,image_angle,c_white,owner.cloakAlpha);
    }
}
