arguments.argument:
if(owner.taunting)
    exit;

var offset, xdrawpos, ydrawpos;
offset = owner.team;
xdrawpos = round(x+xoffset*image_xscale);
ydrawpos = round(y+yoffset);

if (alarm[6] <= 0){
    if (alarm[7] > 0) {
        //if we are not shooting, but airblasting/dropping
        if (sprite_index == dropSprite) {
            offset = image_index mod animationDropOffset + animationDropOffset*owner.team
        }else if (sprite_index == blastSprite){
            offset = image_index mod animationBlastOffset + animationBlastOffset*owner.team
        }
    }else{
        //set the sprite to idle
        offset = owner.team;
    }
}else{
    //We are shooting, loop the shoot animation
    offset = image_index mod animationOffset + animationOffset*owner.team
}


if !owner.player.humiliated draw_sprite_ext(sprite_index,offset, xdrawpos, ydrawpos, image_xscale, image_yscale, image_angle, c_white, 1);
