arguments.argument:
if(owner.taunting or owner.omnomnomnom or owner.player.humiliated)
    exit;

var offset, xdrawpos, ydrawpos;
offset = owner.team;
xdrawpos = round(x+xoffset*image_xscale);
ydrawpos = round(y+yoffset);
if (alarm[6] <= 0){
        //if we are not shooting, but dropping
        if (alarm[7] > 0) {
            offset = image_index mod animationDropOffset + animationDropOffset*owner.team
        }else{
            //set the sprite to idle
            offset = owner.team;
        }
}else{
    //We are shooting, loop the shoot animation
    offset = image_index mod animationOffset + animationOffset*owner.team
}
    draw_sprite_ext(sprite_index,offset, xdrawpos, ydrawpos, image_xscale, image_yscale, image_angle, c_white, 1);
    if (sprite_index == MinigunDropS) {
        draw_sprite_ext(overlayDropSprite,offset, xdrawpos, ydrawpos, image_xscale, image_yscale, image_angle, c_red,min(0.7,1-(ammoCount/maxAmmo)));
    }else{
         draw_sprite_ext(overlaySprite,offset, xdrawpos, ydrawpos, image_xscale, image_yscale, image_angle, c_red,min(0.7,1-(ammoCount/maxAmmo)));
    }
