arguments.argument:
{
    if(readyToShoot) {
        playsound(x,y,ChaingunSnd);
        var shot;
        randomize();

        shot = instance_create(x+lengthdir_x(15,owner.aimDirection),y+lengthdir_y(15,owner.aimDirection),Shot);
        shot.direction=owner.aimDirection+random(24)-12;
        shot.speed=14;
        shot.owner=owner;
        shot.ownerPlayer=ownerPlayer;
        shot.team=owner.team;
        shot.hitDamage = 5;
        shot.weapon=WEAPON_SMG;
        readyToShoot=false;
        alarm[0]=reloadTime;
    }
}
